def test_creation():
    input_1 = TxIn(
        OutPoint("75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858", 0),
        [],
        "",
        256 ** 4 - 1,
        [],
    )
    input_2 = TxIn(
        OutPoint("1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83", 1),
        [],
        "",
        256 ** 4 - 1,
        [],
    )
    output_1 = TxOut(
        149990000, script.decode("0014d85c2b71d0060b09c9886aeb815e50991dda124d")
    )
    output_2 = TxOut(
        100000000, script.decode("001400aea9a2e5f0f876a588df5546e8742d1d87008f")
    )
    transaction = Tx(2, 0, [input_1, input_2], [output_1, output_2])
    psbt = psbt_from_tx(transaction)

    psbt_string = "cHNidP8BAJoCAAAAAljoeiG1ba8MI76OcHBFbDNvfLqlyHV5JPVFiHuyq911AAAAAAD/////g40EJ9DsZQpoqka7CwmK6kQiwHGyyng1Kgd5WdB86h0BAAAAAP////8CcKrwCAAAAAAWABTYXCtx0AYLCcmIauuBXlCZHdoSTQDh9QUAAAAAFgAUAK6pouXw+HaliN9VRuh0LR2HAI8AAAAAAAAAAAA="
    assert psbt.serialize() == psbt_string


def test_psbt_combination():
    combined_psbt_string = ""
    psbt1 = Psbt.deserialize(
        ""
    )
    psbt2 = Psbt.deserialize(
        ""
    )
    combined_psbt = combine_psbts([psbt1, psbt2])
    assert combined_psbt.serialize() == combined_psbt_string
    assert combined_psbt == Psbt.deserialize(combined_psbt_string)


def test_finalization():
    finalized_psbt_string = ""
    psbt = Psbt.deserialize(
        ""
    )
    finalized_psbt = finalize_psbt(psbt)

    assert finalized_psbt.serialize() == finalized_psbt_string
    assert finalized_psbt == Psbt.deserialize(finalized_psbt_string)


def test_extraction():
    tx_string = ""
    psbt_string = ""
    psbt = Psbt.deserialize(psbt_string)
    assert psbt.serialize() == psbt_string
    tx = extract_tx(psbt)
    assert tx.serialize().hex() == tx_string


def test_lexicographic_ordering():
    combined_psbt_string = ""
    psbt1 = Psbt.deserialize(
        "cHNidP8BAD8CAAAAAf//////////////////////////////////////////AAAAAAD/////AQAAAAAAAAAAA2oBAAAAAAAKDwECAwQFBgcICQ8BAgMEBQYHCAkKCwwNDg8ACg8BAgMEBQYHCAkPAQIDBAUGBwgJCgsMDQ4PAAoPAQIDBAUGBwgJDwECAwQFBgcICQoLDA0ODwA="
    )
    psbt2 = Psbt.deserialize(
        "cHNidP8BAD8CAAAAAf//////////////////////////////////////////AAAAAAD/////AQAAAAAAAAAAA2oBAAAAAAAKDwECAwQFBgcIEA8BAgMEBQYHCAkKCwwNDg8ACg8BAgMEBQYHCBAPAQIDBAUGBwgJCgsMDQ4PAAoPAQIDBAUGBwgQDwECAwQFBgcICQoLDA0ODwA="
    )
    combined_psbt = combine_psbts([psbt1, psbt2])
    assert combined_psbt.serialize() == combined_psbt_string
    assert combined_psbt == Psbt.deserialize(combined_psbt_string)


