def test_valid_sign_2():
    psbt_string = ""
    psbt = Psbt.deserialize(psbt_string)
    transaction_input = TxIn(
        OutPoint("75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858", 0),
        [],
        "",
        256 ** 4 - 1,
        [],
    )
    transaction = Tx(0, 2, vin=[transaction_input], vout=[psbt.inputs[0].witness_utxo])
    psbt.tx.vin[0].prevout.hash = transaction.txid
    psbt.inputs[0].non_witness_utxo = transaction
    psbt.assert_signable()


def test_der():
    psbt_string = ""
    psbt = Psbt.deserialize(psbt_string)
    assert (
        psbt.outputs[1].hd_keypaths[
            "027f6399757d2eff55a136ad02c684b1838b6556e5f1b6b34282a94b6b50051096"
        ]["derivation_path"]
        == "m/0h/0h/5h"
    )
