    psbt.inputs[0].proprietary[300] = {"00ff": "00ff"}
    psbt.outputs[1].proprietary[300] = {"ff00": "ff00"}
    psbt.proprietary[300] = {"aaaa": "bbbb"}

    assert Psbt.decode(psbt.encode()) == psbt


