"Tests for the `btclib.psbt_in` module"

import json
from os import path

from btclib.psbt.psbt import Psbt
from btclib.psbt.psbt_in import PsbtIn


def test_psbt_out() -> None:
    psbt_in = PsbtIn()
    # FIXME
    # assert psbt_in == PsbtIn.parse(psbt_in.serialize())
    assert psbt_in == PsbtIn.from_dict(psbt_in.to_dict())


def test_compatibility() -> None:
    psbt_in = PsbtIn()
    assert not psbt_in.sig_hash


def test_dataclasses_json_dict() -> None:

    psbt_str = ""
    psbt = Psbt.b64decode(psbt_str)

    # PsbtIn dataclass
    psbt_in = psbt.inputs[0]
    assert isinstance(psbt_in, PsbtIn)

    # PsbtIn dataclass to dict
    psbt_in_dict = psbt_in.to_dict()
    assert isinstance(psbt_in_dict, dict)
    assert psbt_in_dict["redeem_script"]

    # PsbtIn dataclass dict to file
    datadir = path.join(path.dirname(__file__), "_generated_files")
    filename = path.join(datadir, "psbt_in.json")
    with open(filename, "w") as file_:
        json.dump(psbt_in_dict, file_, indent=4)

    # PsbtIn dataclass dict from file
    with open(filename, "r") as file_:
        psbt_in_dict2 = json.load(file_)
    assert isinstance(psbt_in_dict2, dict)
    assert psbt_in_dict2["redeem_script"]

    assert psbt_in_dict == psbt_in_dict2

    # PsbtIn dataclass from dict
    psbt_in2 = PsbtIn.from_dict(psbt_in_dict)
    assert isinstance(psbt_in2, PsbtIn)

    assert psbt_in == psbt_in2
